$(function () {
  var a = $(window).height();
  $(document).on("scroll", function () {
    $(
      ".section-title, p, .about .section-title, .about__info, .about .about__btn, .about-lt-corner, .about-lb-corner, .ad-photoshop, .ad-illustrator, .skatch, .figma, .ukraine, .english, .spainsh, .russia, .line-services-path, .reviews-line1-path, .reviews-line2-path, .reviews-line3-path, .reviews-line4-path, .blog .blog__link, .blog .blog__iconlike, .blog .blog__iconHeart, .footer__icon, .widget, .comment__avatar img"
    ).each(function () {
      let self = $(this),
        height = self.offset().top + self.height();
      if ($(document).scrollTop() + a >= height) {
        self.addClass("show");
      }
    });
  });
});
